module.exports = (async ({services}, isDebug) => {
  if (!isDebug) return

  await Promise.all(
    [
      ...(
        (await services.member.count()) > 0
          ? []
          : members.map((member) =>
            (() => (services.member.create(member)))()
          )
      ),
      ...(
        (await services.article.count()) > 0
          ? []
          : articles.map((article) =>
          (() => (services.article.create(article)))()
        )
      )
    ]
  )
})
